mapper.select:
SELECT id AS id, code AS code, name AS name, pass AS pass, salt AS salt, status AS status, manager AS manager FROM sys_user
AND id = #{id}
AND code = #{code}
AND name LIKE CONCAT('%',#{name},'%')

SELECT id AS id, code AS code, name AS name, pass AS pass, salt AS salt, status AS status, manager AS manager FROM sys_user
AND id = #{param.id}
AND code = #{param.code}
AND name LIKE CONCAT('%',#{param.name},'%')
